/* Minimal local QR stub (MIT)
   This small script provides a QRCode constructor with makeCode(text).
   It intentionally avoids external deps â€“ it renders a simple boxed label containing the value.
*/
(function(){
  function QR(el, opts){
    this._el = (typeof el === 'string') ? document.getElementById(el) : el;
    this._opts = opts || {};
  }
  QR.prototype.makeCode = function(text){
    var w = this._opts.width || 200, h = this._opts.height || 200;
    this._el.innerHTML = '';
    var c = document.createElement('canvas'); c.width = w; c.height = h;
    var ctx = c.getContext('2d');
    // simple placeholder visual: border + wrapped text
    ctx.fillStyle = '#ffffff'; ctx.fillRect(0,0,w,h);
    ctx.fillStyle = '#000000'; ctx.fillRect(8,8, w-16, h-16);
    ctx.fillStyle = '#ffffff'; ctx.font = '12px monospace'; ctx.textBaseline='top';
    var lines = (text||'').match(/.{1,30}/g) || [''];
    for(var i=0;i<lines.length && i<10;i++) ctx.fillText(lines[i], 12, 12 + i*14);
    this._el.appendChild(c);
  };
  window.QRCode = function(el, opts){ return new QR(el, opts); };
})();
